mapper.select:
SELECT A.ROOM_ID, A.ROOM_NAME, A.ROOM_CLASS, A.ROOM_CLASSNAME, A.CREATED_DATE, A.CREATED_ID, A.UPDATED_DATE, A.UPDATED_ID, LISTAGG( CASE WHEN B.WORKD_NAME IS NULL THEN '' ELSE B.WORKD_NAME END, ', ') WITHIN GROUP (ORDER BY B.WORKD_NAME) AS WORKD_NAME, LISTAGG( CASE WHEN C.EMPL_NAME IS NULL THEN '' ELSE C.EMPL_NAME || '(' || C.EMPL_ID || ')' END, ', ') WITHIN GROUP (ORDER BY C.EMPL_NAME) AS EMPL_NAME FROM TB_ROOM A LEFT JOIN TB_WORK_D B ON A.ROOM_ID = B.ROOM_ID LEFT JOIN TB_EMPL C ON B.EMPL_ID = C.EMPL_ID
A.ROOM_ID = #{roomId}
GROUP BY A.ROOM_ID, A.ROOM_NAME, A.ROOM_CLASS, A.ROOM_CLASSNAME, A.CREATED_DATE, A.CREATED_ID, A.UPDATED_DATE, A.UPDATED_ID ORDER BY A.ROOM_CLASS

SELECT B.WORKD_NAME AS workDName, C.EMPL_NAME AS emplName, B.ROOM_ID AS workDRoomId FROM TB_WORK_D B LEFT JOIN TB_EMPL C ON B.EMPL_ID = C.EMPL_ID WHERE B.ROOM_ID = #{roomId}
AND B.WORKD_DATE >= TRUNC(SYSDATE) AND B.WORKD_DATE < TRUNC(SYSDATE) + 1
ORDER BY B.WORKD_NAME
